import React from "react";
const Total = ({ course }) => {
  let sum = course.parts.reduce((s, p) => s + p.exercises, 0);

  return (
    <div>
      <b>total of {sum} exercises</b>
    </div>
  );
};

const Part = ({ part }) => {
  return (
    <p>
      {part.name} {part.exercises}
    </p>
  );
};

